equalheight=function(t){var i=0,e=0,n=new Array,h,o=0;$(t).each(function(){if(h=$(this),$(h).height("auto"),topPostion=h.position().top,e!=topPostion){for(currentDiv=0;currentDiv<n.length;currentDiv++)n[currentDiv].height(i);n.length=0,e=topPostion,i=h.height(),n.push(h)}else n.push(h),i=i<h.height()?h.height():i;for(currentDiv=0;currentDiv<n.length;currentDiv++)n[currentDiv].height(i)})},$(window).load(function(){equalheight("#client_list li")}),$(window).resize(function(){equalheight("#client_list li")});